import React, { useState } from 'react';
import { TodoItem } from './todo.model';
import { Box, Button, Checkbox } from '@mui/material';
import { ButtonGroup, Input } from '@mui/joy';

interface TodoProps {
    todo: TodoItem;
    deleteTodo: () => void;
    statusTodo: () => void;
}

function Todo({ todo, deleteTodo, statusTodo }: TodoProps) {
    const [edit, setEdit] = useState(false);
    const [value, setValue] = useState('');

    function editTodo(title: string) {
        setEdit(true);
        setValue(title);
    }
    function cancelTodo() {
        setEdit(false);
    }

    function saveTodo() {
        setEdit(false);
        todo.title = value;
    }
    const changeInput = (event: React.ChangeEvent<HTMLInputElement>) => {
        setValue(event.target.value);
    };
    return (
        <Box>
            {edit ? (
                <Box display='flex' justifyContent='left' margin='5px 0'>
                    <Input value={value} onChange={changeInput} size='sm' color='primary' />
                    <ButtonGroup size='sm' spacing={1} sx={{ marginLeft: '5px' }} color='success'>
            <Button onClick={saveTodo}>Save</Button>
            <Button onClick={cancelTodo}>Cancel</Button>
          </ButtonGroup>
                </Box>
            ) : (
                <Box>
                    <Checkbox onChange={statusTodo} />
                    <h3>{todo.title}</h3>
                    <Button onClick={() => editTodo(todo.title)}>Edit</Button>
                    <Button onClick={deleteTodo} color='error'>
                        Delete
                    </Button>
                </Box>
            )}
        </Box>
    );
}

export default Todo;
