import React, { useState, useEffect } from 'react';
import { Typography, Button, TextField } from '@mui/material';
import { Box } from '../../assets/styles/components';
import Btn from './Btn';
const Countdown: React.FC = () => {
    const [minutes, setMinutes] = useState(0);
    const [seconds, setSeconds] = useState(0);

    const [minutesInput, setMinutesInput] = useState(0);
    const [secondsInput, setSecondsInput] = useState(0);

    const [isRunning, setIsRunning] = useState(false);
    const [progress, setProgress] = useState(100);
    
    function playAudio() {
        const audioUrl = 'https://s3.amazonaws.com/freecodecamp/drums/Heater-1.mp3';
        const audio = new Audio(audioUrl);
        audio
            .play()
            .then(() => {
                console.log('Аудио воспроизводится');
            })
            .catch(error => {
                console.error('Произошла ошибка при воспроизведении аудио:', error);
            });
    }

    // Обновляем минуты и секунды при изменении input
    useEffect(() => {
        if (!isRunning) {
            setMinutes(minutesInput);
            setSeconds(secondsInput);
        }
    }, [minutesInput, secondsInput, isRunning]);

    // Таймер
    useEffect(() => {
        let interval: NodeJS.Timer;
        if (isRunning) {
            interval = setInterval(() => {
                const totalSeconds = minutes * 60 + seconds;
                const passedSeconds = minutesInput * 60 + secondsInput - totalSeconds;
                
                const progressValue = Math.floor((passedSeconds / (minutesInput * 60 + secondsInput)) * 100);
                if (seconds > 0) {
                    setSeconds(seconds - 1);
                } else if (minutes > 0) {
                    setMinutes(minutes - 1);
                    setSeconds(59);
                } else {
                    clearInterval(interval);
                    playAudio();
                   
                }
                setProgress(progressValue);
            }, 1000);
        }
        return () => clearInterval(interval);
    }, [isRunning, minutes, seconds]);

    // Сброс таймера
    const reset = () => {
        setMinutesInput(0);
        setSecondsInput(0);
        setMinutes(0);
        setSeconds(0);
        setIsRunning(false);
    };

    // Переключение таймера
    const toggle = () => {
        if (!isRunning) {
            setIsRunning(true);
        } else {
            setIsRunning(false);
        }
    };

    return (
        <Box>
            <TextField
                type='number'
                disabled={isRunning}
                value={minutesInput}
                onChange={e => setMinutesInput(parseInt(e.target.value))}
            />

            <TextField
                disabled={isRunning}
                color='secondary'
                type='number'
                value={secondsInput}
                onChange={e => setSecondsInput(parseInt(e.target.value))}
            />
<progress value={progress} />
            <Typography variant='h4'>
                {minutes}:{seconds < 10 ? `0${seconds}` : seconds}
            </Typography>
            <Btn isRunning={isRunning} toggle={toggle} reset={reset} />
        </Box>
    );
};

export default Countdown;
