import React, { useState, useEffect } from 'react';
import { Box, Typography, Button, TextField } from '@mui/material';

const Countdown: React.FC = () => {

  const [minutes, setMinutes] = useState(0);
  const [seconds, setSeconds] = useState(0);

  const [minutesInput, setMinutesInput] = useState(0);
  const [secondsInput, setSecondsInput] = useState(0);

  const [isRunning, setIsRunning] = useState(false);
  const [isInit, setIsInit] = useState(false)



  // Обновляем минуты и секунды при изменении input
  useEffect(() => {
    setMinutes(minutesInput);
    setSeconds(secondsInput);
  }, [minutesInput, secondsInput])

  // Таймер
  useEffect(() => {
    let interval: NodeJS.Timer;
    if (isRunning) {
      interval = setInterval(() => {
        if (seconds > 0) {
          setSeconds(seconds - 1);
        } else if (minutes > 0) {
          setMinutes(minutes - 1);
          setSeconds(59);
        }
      }, 1000);
    }
    return () => clearInterval(interval);
  }, [isRunning, minutes, seconds]);

  // Сброс таймера
  const reset = () => {
    setMinutesInput(0);
    setSecondsInput(0);
    setIsRunning(false);
  };

  // Переключение таймера
  const toggle = () => {
    if (!isRunning) {
        setIsRunning(true);
    } else {
        setIsRunning(false)
    };
  };

  return (
    <Box>
      <TextField
        type="number"
        value={minutesInput}
        onChange={(e) => setMinutesInput(parseInt(e.target.value))}
      />

      <TextField
        type="number" 
        value={secondsInput}
        onChange={(e) => setSecondsInput(parseInt(e.target.value))}
      />

      <Typography variant="h4">
        {minutes}:{seconds < 10 ? `0${seconds}минут` : seconds} 
      </Typography>

      <Button onClick={toggle}>
        {isRunning ? 'Пауза' : 'Старт'}
      </Button>
      <Button onClick={reset}>Сброс</Button>

    </Box>
  );
}

export default Countdown;
