import { Button } from '@mui/material';
import React from 'react';

interface IBtnSetting {
    isRunning: boolean;
    toggle: () => void;
    reset: () => void;
}

function Btn({ isRunning, toggle, reset }: IBtnSetting) {
    return (
        <>
               <hr></hr>
            <Button 
            onClick={toggle}>{isRunning ? 'Пауза' : 'Старт'}
            </Button>
            <Button color='secondary' onClick={reset}>
                Сброс
            </Button>
        </>
    );
}

export default Btn;
