import {SApp, SHeader} from "./assets/styles/app.styles";
import TodoList from './components/TodoList';
import TodoCounter from './components/TodoCounter';
import { useEffect, useState } from 'react';
import { TodoItem } from './components/todo.model';
import TodoAdd from "./components/TodoAdd";
import TodoFilter from "./components/TodoFilter";



function App() {
 
const [todos, setTodos] = useState<TodoItem[]>  (JSON.parse(localStorage.getItem('todo list')))
const [filtered, setFiltered] = useState('all')

useEffect(() => {
   localStorage.setItem('todo list', JSON.stringify(todos));
 }, [todos]);

function todoFilter(todos: any, filtered: any) {
   if(filtered === 'all') {
      return todos
   }
   if(filtered === 'active') {
      return todos.filter((todo: { completed: any; }) => !todo.completed)
   }
   if(filtered === 'completed') {
      return todos.filter((todo: { completed: any; }) => !todo.completed)
   }
   throw new Error('Filter error')
}

    return (
        <SApp>
            <SHeader>
            <h1>Todo List</h1>
            <TodoAdd todos={todos} setTodos={setTodos}/>
            <TodoFilter setFiltered={setFiltered}/>
            <TodoCounter count={todos.length}/>
            <TodoList todos={todoFilter(todos, filtered)} setTodos={setTodos}/>
            </SHeader>
        </SApp>
    );
}

export default App;
