import React from 'react';

interface InputBlockProps {
    minutesInput: number;
    secondsInput: number;
    onTimeChange: (minutesInput: number, secondsInput: number) => void;
    isRunning: boolean;
}


 InputMain = ({ minutesInput, secondsInput, onTimeChange, isRunning }: InputBlockProps) => {

    const handleMinutesChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        const newMinutes = parseInt(e.target.value, 10);
        if (!isNaN(newMinutes)) {
            onTimeChange(newMinutes, secondsInput);
        }
    };
    const handleSecondsChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        const newSeconds = parseInt(e.target.value, 10);
        if (!isNaN(newSeconds)) {
            onTimeChange(minutesInput, newSeconds);
        }
    };
    return (
        <div>
               <label >
                Minutes:
                <input
                  type='number' 
                  value={minutesInput}
                  onChange={e => handleMinutesChange(parseInt(e.target.value))}  />
          
           
            </label>
            <label>
                Seconds:
                <input
                  color='secondary'
                  type='number'
                  value={secondsInput}
                  onChange={e => handleSecondsChange(parseInt(e.target.value))}/>
                </label>
        </div>
    );
};

export default InputMain;
