import React, { useState, useEffect } from 'react';
import { Typography, Slider, TextField } from '@mui/material';

interface CountdownProps {}

const Countdown: React.FC<CountdownProps> = () => {
  const [minutes, setMinutes] = useState(0);
  const [seconds, setSeconds] = useState(0);

  const [minutesInput, setMinutesInput] = useState(0);
  const [secondsInput, setSecondsInput] = useState(0);

  const [sliderMinutes, setSliderMinutes] = useState(0);

  const handleMinutesChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    let value = parseInt(e.target.value);
    if (value > 720) {
      value = 720; 
    }
    setMinutesInput(value);
  };

  useEffect(() => {
    setSliderMinutes(minutesInput);
  }, [minutesInput]);

  return (
    <>
      <Slider
        min={0} 
        max={60}
        step={15}
        value={sliderMinutes}
        onChange={(e, newValue) => {
          setSliderMinutes(newValue);
          setMinutesInput(Math.floor(newValue));
          setSecondsInput(Math.round((newValue - Math.floor(newValue)) * 60));
        }}
      />

      <TextField
        type="number"
        value={minutesInput}
        onChange={handleMinutesChange} 
      />

      <TextField
        type="number" 
        value={secondsInput}
      />

      <Typography>
        {minutes}:{seconds < 10 ? `0${seconds}` : seconds}
      </Typography>
    </>
  );
}

export default Countdown;
